private static final double  DEFAULT_SINGLE_NULL_DISTANCE = onPremiseRoundError(0.5);
private static final double  DEFAULT_DOUBLE_NULL_DISTANCE = onPremiseRoundError(0.5);

private double  singleNullDistanceScore = DEFAULT_SINGLE_NULL_DISTANCE;
private double  doubleNullDistanceScore = DEFAULT_DOUBLE_NULL_DISTANCE;
private boolean initialized = false;

/*
 * There is a bug in the on-premise product where the single / double null distance values
 * have a float to double rounding error. For example, if the user specifies one of the 
 * values to be 0.9, the actual value output is 0.8999999761581421. This is because somewhere
 * in the on-premise product, the initial "0.9" is a float that gets converted the output
 * data type of double.
 */
private static double onPremiseRoundError(final double val) {
  float f = (float)val;
  return (double)f;
}

private double computeEditDistance(final String sA, final String sB) {
  // We want A to be the longer string
  String stringA = sA;
  String stringB = sB;
  if (stringB.length() > stringA.length()) {
    stringA = sB;
    stringB = sA;
  }

  int lenA = stringA.length();
  int lenB = stringB.length();

  int [] editTable = new int[lenB + 1];

  for (int iB = 0; iB <= lenB; iB++) {
    editTable[iB] = iB;
  }

  for (int iA = 1; iA <= lenA; iA++) {
    int lastTickVal = editTable[0];
    editTable[0] = iA;
    for (int iB = 1; iB <= lenB; iB++) {
      final int cost = (stringA.charAt(iA - 1) == stringB.charAt(iB - 1)) ? 0 : 1;
      final int newTickVal = Math.min(editTable[iB - 1] + 1, 
                                      Math.min(editTable[iB] + 1, lastTickVal + cost));
      lastTickVal = editTable[iB];
      editTable[iB] = newTickVal;
    }
  }

  return 1.0 - (double)(editTable[lenB]) / (double)lenA;
}

